import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import { FaBicycle, FaArrowRight, FaStar, FaUsers, FaRoad, FaMountain, FaGraduationCap } from 'react-icons/fa';
import axios from 'axios';
import './SaddleDesigns.css';

const SaddleDesigns = () => {
  const [saddles, setSaddles] = useState([]);
  const [loading, setLoading] = useState(true);
  const [activeSaddle, setActiveSaddle] = useState(0);

  useEffect(() => {
    fetchSaddles();
  }, []);

  const fetchSaddles = async () => {
    try {
      const response = await axios.get('/api/saddles');
      setSaddles(response.data);
      setLoading(false);
    } catch (error) {
      console.error('Error fetching saddles:', error);
      setLoading(false);
    }
  };

  const scrollToSaddle = (index) => {
    setActiveSaddle(index);
    const element = document.getElementById(`saddle-${index}`);
    if (element) {
      element.scrollIntoView({ behavior: 'smooth', block: 'start' });
    }
  };

  const getRiderProfile = (saddle) => {
    switch (saddle.category) {
      case 'Racing':
        return {
          icon: <FaRoad />,
          title: 'Racing Enthusiasts',
          description: 'Competitive cyclists who prioritize speed, power transfer, and aerodynamic efficiency. Perfect for road racing, time trials, and high-intensity training sessions.',
          characteristics: ['Aggressive riding position', 'High cadence pedaling', 'Long-distance events', 'Performance-focused mindset']
        };
      case 'Comfort':
        return {
          icon: <FaUsers />,
          title: 'Comfort Seekers',
          description: 'Riders who value comfort over speed, perfect for commuting, touring, and recreational cycling. Ideal for those who spend long hours in the saddle.',
          characteristics: ['Upright riding position', 'Leisurely pace', 'Daily commuting', 'Weekend touring']
        };
      case 'Mountain':
        return {
          icon: <FaMountain />,
          title: 'Mountain Warriors',
          description: 'Off-road riders who tackle challenging terrain and need durability. Built for technical trails, jumps, and rough conditions.',
          characteristics: ['Technical trail riding', 'Aggressive descending', 'Jump and drop handling', 'All-weather durability']
        };
      case 'Gravel':
        return {
          icon: <FaGraduationCap />,
          title: 'Adventure Seekers',
          description: 'Versatile riders who explore mixed terrain and seek adventure. Perfect for gravel roads, light trails, and long-distance exploration.',
          characteristics: ['Mixed terrain riding', 'Long-distance adventures', 'Exploration focus', 'Versatile performance']
        };
      default:
        return {
          icon: <FaBicycle />,
          title: 'General Riders',
          description: 'Suitable for a wide range of riding styles and preferences.',
          characteristics: ['Versatile use', 'Balanced performance', 'Wide compatibility', 'Entry-level friendly']
        };
    }
  };

  if (loading) {
    return (
      <div className="saddle-designs">
        <div className="container">
          <div className="loading-container">
            <div className="spinner"></div>
            <p>Loading saddle designs...</p>
          </div>
        </div>
      </div>
    );
  }

  return (
    <div className="saddle-designs">
      <div className="container">
        {/* Header */}
        <div className="page-header">
          <h1>Discover Your Perfect Saddle</h1>
          <p>Scroll through our carefully crafted saddle designs, each tailored for specific rider profiles and riding styles</p>
        </div>

        {/* Navigation Dots */}
        <div className="saddle-navigation">
          {saddles.map((saddle, index) => (
            <button
              key={saddle.id}
              className={`nav-dot ${activeSaddle === index ? 'active' : ''}`}
              onClick={() => scrollToSaddle(index)}
              title={saddle.name}
            >
              <span className="dot-number">{index + 1}</span>
              <span className="dot-label">{saddle.name}</span>
            </button>
          ))}
        </div>

        {/* Saddles Horizontal Layout */}
        <div className="saddles-horizontal">
          {saddles.map((saddle, index) => {
            const riderProfile = getRiderProfile(saddle);
            return (
              <div 
                key={saddle.id} 
                id={`saddle-${index}`}
                className="saddle-card-compact"
              >
                <div className="saddle-image-compact">
                  <img 
                    src={saddle.image} 
                    alt={`${saddle.name} saddle`}
                    onError={(e) => {
                      e.target.style.display = 'none';
                      e.target.nextSibling.style.display = 'flex';
                    }}
                  />
                  <div className="image-placeholder-compact" style={{ display: 'none' }}>
                    <FaBicycle />
                  </div>
                </div>
                
                <div className="saddle-content-compact">
                  <div className="saddle-header">
                    <div className="saddle-category-badge">{saddle.category}</div>
                    <h3 className="saddle-name">{saddle.name}</h3>
                    <div className="saddle-price">${saddle.price}</div>
                  </div>
                  
                  <p className="saddle-description">{saddle.description}</p>
                  
                  <div className="rider-profile-compact">
                    <div className="profile-icon">
                      {riderProfile.icon}
                    </div>
                    <div className="profile-info">
                      <h4>{riderProfile.title}</h4>
                      <p>{riderProfile.description}</p>
                    </div>
                  </div>
                  
                  <div className="key-features">
                    <h4>Key Features:</h4>
                    <ul>
                      <li>Premium construction</li>
                      <li>Optimized padding</li>
                      <li>Ergonomic shape</li>
                    </ul>
                  </div>
                  
                  <Link 
                    to={`/order?saddleId=${saddle.id}`} 
                    className="btn btn-primary btn-compact"
                  >
                    Order Now
                    <FaArrowRight />
                  </Link>
                </div>
              </div>
            );
          })}
        </div>

        {/* CTA Section */}
        <div className="cta-section">
          <h2>Found Your Perfect Match?</h2>
          <p>Each saddle is designed with specific riders in mind. Take your time to explore and find the one that matches your riding style and comfort needs.</p>
          <Link to="/order" className="btn btn-primary btn-large">
            Start Your Custom Order
            <FaArrowRight />
          </Link>
        </div>
      </div>
    </div>
  );
};

export default SaddleDesigns;
