import React, { useState, useEffect } from 'react';
import { FaBicycle } from 'react-icons/fa';
import axios from 'axios';
import './SaddleDesigns.css';

const SaddleDesigns = () => {
  const [saddles, setSaddles] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    fetchSaddles();
  }, []);

  const fetchSaddles = async () => {
    try {
      const response = await axios.get('/api/saddles');
      setSaddles(response.data);
      setLoading(false);
    } catch (error) {
      console.error('Error fetching saddles:', error);
      setLoading(false);
    }
  };



  if (loading) {
    return (
      <div className="saddle-designs">
        <div className="container">
          <div className="loading-container">
            <div className="spinner"></div>
            <p>Loading saddle designs...</p>
          </div>
        </div>
      </div>
    );
  }

  return (
    <div className="saddle-designs">
      <div className="container">
        {/* Header */}
        <div className="page-header">
          <h1>Discover Your Perfect Saddle</h1>
          <p>Scroll through our carefully crafted saddle designs, each tailored for specific rider profiles and riding styles</p>
        </div>

        {/* Navigation Dots */}
        <div className="saddle-navigation">
          {saddles.map((saddle, index) => (
            <button
              key={saddle.id}
              className={`nav-dot ${activeSaddle === index ? 'active' : ''}`}
              onClick={() => scrollToSaddle(index)}
              title={saddle.name}
            >
              <span className="dot-number">{index + 1}</span>
              <span className="dot-label">{saddle.name}</span>
            </button>
          ))}
        </div>

        {/* Saddles Horizontal Layout */}
        <div className="saddles-horizontal">
          {saddles.map((saddle, index) => {
            const riderProfile = getRiderProfile(saddle);
            return (
              <div 
                key={saddle.id} 
                id={`saddle-${index}`}
                className="saddle-card-compact"
              >
                <div className="saddle-image-compact">
                  <img 
                    src={saddle.image} 
                    alt={`${saddle.name} saddle`}
                    onError={(e) => {
                      e.target.style.display = 'none';
                      e.target.nextSibling.style.display = 'flex';
                    }}
                  />
                  <div className="image-placeholder-compact" style={{ display: 'none' }}>
                    <FaBicycle />
                  </div>
                </div>
                
                                 <div className="saddle-content-compact">
                   <h3 className="saddle-name">{saddle.name}</h3>
                   <div className="saddle-price">${saddle.price}</div>
                 </div>
              </div>
            );
          })}
        </div>

        {/* CTA Section */}
        <div className="cta-section">
          <h2>Found Your Perfect Match?</h2>
          <p>Each saddle is designed with specific riders in mind. Take your time to explore and find the one that matches your riding style and comfort needs.</p>
          <Link to="/order" className="btn btn-primary btn-large">
            Start Your Custom Order
            <FaArrowRight />
          </Link>
        </div>
      </div>
    </div>
  );
};

export default SaddleDesigns;
